import {
	START,
	SUCCESS,
	FAILURE,
	LOGIN,
	LOGOUT
} from 'constants.js'

export function actionLogin(form){
	const {login, password} = form
	return (dispatch) => {
		dispatch({
			type: LOGIN+START
		})
		setTimeout( () => {
			if( login == 'admin' && password == 12345 ){
				dispatch({
					type: LOGIN+SUCCESS
				})
			}else{
				dispatch({
					type: LOGIN+FAILURE
				})
			}
		}, 1500)
	}
}

export function actionLogout(){
	return({
		type: LOGOUT
	})
}