import React, {Component} from 'react'
import {
	Form,
	FormGroup,
	Label,
	Input,
	Button
} from 'reactstrap'

class Login extends Component{
	constructor(props){
		super(props)
		this.state = {
			text: "",
			password: ""
		}
	}
	onChangeInput = (name, e) => {
		const value = e.target.value
		this.setState( prevState => {
			return { [name]: value }
		})
	}
	onSubmit = (e) => {
		e.preventDefault()
		this.setState( prevState => {
			return {
				text: '',
				password: ''
			}
		})
	}
	render(){
		return(
			<div className="container">
				<div className="row">
					<div className="col-md-4">
						<Form>
			        <FormGroup>
			          <Label for="exampleEmail">Login</Label>
			          <Input type="text" name="login" id="exampleEmail" value={this.state.text} onChange={this.onChangeInput.bind(null, 'text')} />
			        </FormGroup>
			        <FormGroup>
			          <Label for="examplePassword">Password</Label>
			          <Input type="password" name="password" id="examplePassword" value={this.state.password} onChange={this.onChangeInput.bind(null, 'password')} />
			        </FormGroup>
			        <Button onClick={this.onSubmit}>Login</Button>
			      </Form>
			    </div>
		    </div>
			</div>
		)
	}
}

export default Login