import {
	START,
	SUCCESS,
	FAILURE,
	LOGIN,
	LOGOUT
} from 'constants.js'

const initialState = {
	login: false,
	connecting: false,
	error: false,
}

export default (state = initialState, action) => {
	const {type} = action
	switch(type){
		case LOGIN+START:
			return {...state, connecting: true, error: false}
		case LOGIN+SUCCESS:
			return {...state, connecting: false, login: true}
		case LOGIN+FAILURE:
			return {connecting: false, login: false, error: true}
		case LOGOUT:
			return {...state, connecting: false, login: false}
		default:
			return state
	}
}