import {
	START,
	SUCCESS,
	FAILURE,
	NEWS
} from 'constants.js'


const initialState = {
	loading: false,
	loaded: false,
	error: false,
	data: []
}


export default (state = initialState, action) => {
	const {type} = action;
	switch( type ){
		case NEWS+START:
			return {
				...state,
				loading: true,
				loaded: false,
				error: false,
				data: []
			};
		case NEWS+SUCCESS:
			return {
				...state,
				loading: false,
				loaded: true,
				error: false,
				data: action.payload
			};
		case NEWS+FAILURE:
			return {
				...state,
				loading: false,
				loaded: false,
				error: true,
				data: []
			};
		default:
			return state;
	}
}