import {
	START,
	SUCCESS,
	FAILURE,
	NEWS
} from 'constants.js'


const initialState = {
	loading: false,
	loaded: false,
	error: false,
	data: {}
}


export default (state = {}, action) => {
	const {type} = action;
	switch( type ){
		case NEWS+START:
			return state;
		case NEWS+SUCCESS:
			return state;
		case NEWS+FAILURE:
			return state;
		default:
			return state;
	}
}