import {
	START,
	SUCCESS,
	FAILURE,
	PERSONAL_INFO
} from 'constants.js'

const initialState = {
	error: false,
	loading: false,
	data: false,
}

export default function personalInfo(state = initialState, action){
	const {type, payload} = action
	switch(type){
		case PERSONAL_INFO+START:
			return {
				...state,
				error: false,
				loading: true,
			}
		case PERSONAL_INFO+SUCCESS:
			return {
				error: false,
				loading: false,
				data: {...payload}
			};
		case PERSONAL_INFO+FAILURE:
			return {
				...state,
				error: true,
				loading: false,
			}
		default:
			return state;
	}
}